<?php $ar = file_get_contents('grabstat'); $xe7f0X67C = unserialize(urldecode($ar)); $ok = $xe7f0X67C['ok']; $ng = $xe7f0X67C['ng']; $okcnt = number_format($xe7f0X67C['okcnt']); $ngcnt = number_format($xe7f0X67C['ngcnt']); $url = $xe7f0X67C['url']; $url = substr($url,7); $fldr = $xe7f0X67C['fldr']; $sub = $xe7f0X67C['sub']; $path = $xe7f0X67C['path']; $ar_ok = explode('<br>',$ok); $ar_ng = explode('<br>',$ng); $br = "\n"; $str1 = ""; $str2 = ""; if(($okcnt != 0)&&($ngcnt != 0)) { $ht = '150px'; $dsp = 'block'; $hdr1 = "Grabbed files: $okcnt"; for($x=0;$x<$okcnt;$x++) { $sok = $ar_ok[$x]; $xok = ($path == 'yes')? $sok : basename($sok); $str1 .= "$url/$sok --> $fldr/$sub$xok$br"; } for($x=0;$x<$ngcnt;$x++) { $sng = $ar_ng[$x]; $str2 .= "$url/$sng$br"; } } else { $ht = '336px'; $dsp = 'none'; if($okcnt != 0) { $hdr1 = "Grabbed files: $okcnt"; for($x=0;$x<$okcnt;$x++) { $sok = $ar_ok[$x]; $xok = ($path == 'yes')? $sok : basename($sok); $str1 .= "$url/$sok --> $fldr/$sub$xok$br"; } } else { $hdr1 = 'Files not found: '.$ngcnt; for($x=0;$x<$ngcnt;$x++) { $sng = $ar_ng[$x]; $str1 .= "$url/$sng$br"; } } } $thm_sbbody = $_GET['thm_sbbody']; $thm_text = $_GET['thm_text']; $thm_inpColor = $_GET['thm_inpColor']; $thm_inpBgnd = $_GET['thm_inpBgnd']; $thm_inpBord = $_GET['thm_inpBord']; ?> <!DOCTYPE html> <html> <head> <meta charset="utf-8" /> <title>-</title> <style type="text/css"> body { background: <?php echo $thm_sbbody ?>; color: <?php echo $thm_text ?>; font-family: Verdana,Arial,Helvetica,sans-serif; margin: 0; padding: 0; } .dia-content { font-size:12.8px; padding:20px 20px 0; text-align:left; white-space:nowrap; } .grabbox { width:100%; height:100%; padding:2px 4px 2px 4px; overflow-y:scroll; font-family: monospace; font-size:12px; resize:none; white-space:nowrap; color:<?php echo $thm_inpColor ?>; background-color:<?php echo $thm_inpBgnd ?>; border-color:<?php echo $thm_inpBord ?>; border-style:solid; border-width:1px; border-radius:10px; -moz-border-radius:10px; -webkit-border-radius:10px; } </style></head> <body>  <div class="dia-content" spellcheck="false"> <div style="margin-bottom:1px">&nbsp;<?php echo $hdr1 ?></div> <div style="height:<?php echo $ht ?>;margin:0 8px 0 0"><textarea class="grabbox" wrap="off" title="Path/filenames (separate lines)"><?php echo $str1 ?></textarea></div> <div style="width:100%;height:18px"></div>  <div style="display:<?php echo $dsp ?>"> <div style="margin-bottom:1px">&nbsp;Files not found: <?php echo $ngcnt ?></div> <div style="height:150px;margin:0 8px 0 0"><textarea class="grabbox" wrap="off" title="Path/filenames (separate lines)"><?php echo $str2 ?></textarea></div> </div>  </div> </body> </html>